import { createContext, useContext, ReactNode } from "react"
import { ContextProps } from "../types"

const SwitchContext = createContext<ContextProps>({})

export const useSwitchContext = () =>
  useContext(SwitchContext)

/**
 * Context to wrap the Cases components and provide the comparison operand
 * @param on string | boolean | number
 * @returns ReactNode
 * @children <Cases />
 */
const Switch = ({
  children,
  on,
}: {
  children: ReactNode
  on: number | string | boolean
}) => {
  return (
    <SwitchContext.Provider value={{ on }}>
      {children}
    </SwitchContext.Provider>
  )
}

export default Switch
